	static String getImageBinary() {
		File f = new File("f://1.jpg");
		BufferedImage bi;
		try {
			bi = ImageIO.read(f);
			ByteArrayOutputStream baos = new ByteArrayOutputStream();
			ImageIO.write(bi, "jpg", baos);
			byte[] bytes = baos.toByteArray();

			return encoder.encodeBuffer(bytes).trim();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return null;
	}

	static void base64StringToImage(String base64String) {
		try {
			byte[] bytes1 = decoder.decodeBuffer(base64String);
			ByteArrayInputStream bais = new ByteArrayInputStream(bytes1);
			BufferedImage bi1 = ImageIO.read(bais);
			File w2 = new File("f://2.png");// 可以是jpg,png,gif格式
			ImageIO.write(bi1, "jpg", w2);// 不管输出什么格式图片，此处不需改动
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	public static String getImageString(byte[] data) throws IOException {
		BASE64Encoder encoder = new BASE64Encoder();
		return data != null ? encoder.encode(data) : "";
	}

	public static byte[] getStringImage(String base64String) throws IOException {
		BASE64Decoder decoder = new sun.misc.BASE64Decoder();
		return base64String != null ? decoder.decodeBuffer(base64String) : null;
	}

	 public static String getBASE64(String s) {
	        if (s == null) {
	            return null;
	        }

	        return (new sun.misc.BASE64Encoder()).encode(s.getBytes());
	    }

